def get_avatar(role):
    if role == "user":
        return "https://64.media.tumblr.com/cc325cc258eb3731705fdb44ec25d5b9/52f8a85d7214aa40-a2/s400x600/21d3b7db5691f45ac2fa9f568c81b81865275633.png"
    elif role == "assistant":
        return "./images/venti.png"
    else:
        return None
    
